pragma solidity ^0.8.7;

import "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";

library Weather {
    // Get temperature in Celsius (multiplied by 100 to avoid decimals)
    function getTemperature(
        AggregatorV3Interface temperatureFeed
    ) internal view returns (int256) {
        (, int256 temperature, , , ) = temperatureFeed.latestRoundData();
        return temperature;
    }

    // Get humidity as percentage (multiplied by 100)
    function getHumidity(
        AggregatorV3Interface humidityFeed
    ) internal view returns (uint256) {
        (, int256 humidity, , , ) = humidityFeed.latestRoundData();
        require(humidity >= 0, "Invalid humidity data");
        return uint256(humidity);
    }

    // Get rainfall in mm (multiplied by 100 to avoid decimals)
    function getRainfall(
        AggregatorV3Interface rainfallFeed
    ) internal view returns (uint256) {
        (, int256 rainfall, , , ) = rainfallFeed.latestRoundData();
        require(rainfall >= 0, "Invalid rainfall data");
        return uint256(rainfall);
    }

    // Get wind speed in km/h (multiplied by 100 to avoid decimals)
    function getWindSpeed(
        AggregatorV3Interface windSpeedFeed
    ) internal view returns (uint256) {
        (, int256 windSpeed, , , ) = windSpeedFeed.latestRoundData();
        require(windSpeed >= 0, "Invalid wind speed data");
        return uint256(windSpeed);
    }

    // Check if temperature is within acceptable range for farming
    function isTemperatureOptimal(
        AggregatorV3Interface temperatureFeed,
        int256 minTemp,
        int256 maxTemp
    ) internal view returns (bool) {
        int256 currentTemp = getTemperature(temperatureFeed);
        return currentTemp >= minTemp && currentTemp <= maxTemp;
    }

    // Check if humidity is within acceptable range
    function isHumidityOptimal(
        AggregatorV3Interface humidityFeed,
        uint256 minHumidity,
        uint256 maxHumidity
    ) internal view returns (bool) {
        uint256 currentHumidity = getHumidity(humidityFeed);
        return currentHumidity >= minHumidity && currentHumidity <= maxHumidity;
    }

    // Get comprehensive weather data in a single call
    struct WeatherData {
        int256 temperature;
        uint256 humidity;
        uint256 rainfall;
        uint256 windSpeed;
        uint256 timestamp;
    }

    function getWeatherData(
        AggregatorV3Interface temperatureFeed,
        AggregatorV3Interface humidityFeed,
        AggregatorV3Interface rainfallFeed,
        AggregatorV3Interface windSpeedFeed
    ) internal view returns (WeatherData memory) {
        return
            WeatherData({
                temperature: getTemperature(temperatureFeed),
                humidity: getHumidity(humidityFeed),
                rainfall: getRainfall(rainfallFeed),
                windSpeed: getWindSpeed(windSpeedFeed),
                timestamp: block.timestamp
            });
    }

    // Check if weather conditions are suitable for farming operations
    function isFarmingConditionsSuitable(
        AggregatorV3Interface temperatureFeed,
        AggregatorV3Interface humidityFeed,
        AggregatorV3Interface rainfallFeed,
        int256 minTemp,
        int256 maxTemp,
        uint256 minHumidity,
        uint256 maxHumidity,
        uint256 maxRainfall
    ) internal view returns (bool) {
        WeatherData memory weather = getWeatherData(
            temperatureFeed,
            humidityFeed,
            rainfallFeed,
            AggregatorV3Interface(address(0)) // windSpeed not needed for this check
        );

        return (weather.temperature >= minTemp &&
            weather.temperature <= maxTemp &&
            weather.humidity >= minHumidity &&
            weather.humidity <= maxHumidity &&
            weather.rainfall <= maxRainfall);
    }
}
","keccak256":"0xcd4e3824d7f59fb62598f91c5f4ebe4b5957c30b586b890d1998ea1e73d3a919","license":"MIT"},"src/SmartContracts/Product.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./StakeholderRegistry.sol";
import "./Stakeholder.sol";
import "./Oracles/Weather.sol";
import "./Oracles/Price.sol";
import "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";

contract Product {
    using Weather for AggregatorV3Interface;
    using Price for AggregatorV3Interface;

    enum ProductStage {
        FARM,
        PROCESSING,
        DISTRIBUTION,
        RETAIL,
        CONSUMED
    }

    struct StageData {
        address stakeholder;
        uint32 timestamp;
        string data;
        bytes32 dataHash;
        Weather.WeatherData weatherAtStage;
        uint256 marketPriceAtStage;
    }

    struct OracleFeeds {
        AggregatorV3Interface temperatureFeed;
        AggregatorV3Interface humidityFeed;
        AggregatorV3Interface rainfallFeed;
        AggregatorV3Interface windSpeedFeed;
        AggregatorV3Interface priceFeed;
    }

    // Product basic info
    string public name;
    string public description;
    uint256 public minCTemperature;
    uint256 public maxCTemperature;
    string public location;

    // Product state
    address public farmer;
    uint32 public createdAt;
    ProductStage public currentStage;
    bool public isActive;
    bytes32 public dataHash;
    uint256 public estimatedPrice;

    // Stage tracking
    mapping(ProductStage => StageData) public productStages;

    // External contracts
    StakeholderRegistry public immutable stakeholderRegistry;
    OracleFeeds public oracleFeeds;

    // Events
    event ProductStageUpdated(
        ProductStage indexed stage,
        address indexed stakeholder,
        string data,
        uint32 timestamp,
        Weather.WeatherData weatherData,
        uint256 marketPrice
    );

    event WeatherAlert(
        string alertType,
        string message,
        Weather.WeatherData weatherData,
        uint32 timestamp
    );

    event PriceAlert(
        uint256 currentPrice,
        uint256 alertThreshold,
        string priceMovement,
        uint32 timestamp
    );

    // Modifiers
    modifier onlyRegisteredStakeholder(
        Stakeholder.StakeholderRole _requiredRole
    ) {
        require(
            stakeholderRegistry.isRegisteredStakeholder(
                msg.sender,
                _requiredRole
            ),
            "Not registered for this role"
        );
        _;
    }

    modifier validStageTransition(ProductStage _newStage) {
        require(
            uint(_newStage) == uint(currentStage) + 1,
            "Invalid stage transition"
        );
        _;
    }

    modifier nonEmptyString(string calldata _str) {
        require(bytes(_str).length > 0, "String cannot be empty");
        _;
    }

    constructor(
        string memory _name,
        string memory _description,
        uint256 _minCTemperature,
        uint256 _maxCTemperature,
        string memory _location,
        string memory _farmData,
        address _stakeholderRegistry,
        address _temperatureFeed,
        address _humidityFeed,
        address _rainfallFeed,
        address _windSpeedFeed,
        address _priceFeed
    ) {
        require(bytes(_name).length > 0, "Product name cannot be empty");
        require(bytes(_description).length > 0, "Product description cannot be empty");
        require(bytes(_location).length > 0, "Product location cannot be empty");
        
        name = _name;
        description = _description;
        minCTemperature = _minCTemperature;
        maxCTemperature = _maxCTemperature;
        location = _location;
        farmer = tx.origin; // The farmer who initiated the factory call
        createdAt = uint32(block.timestamp);
        currentStage = ProductStage.FARM;
        isActive = true;
        dataHash = keccak256(abi.encodePacked(_name, _farmData));

        stakeholderRegistry = StakeholderRegistry(_stakeholderRegistry);

        oracleFeeds = OracleFeeds({
            temperatureFeed: AggregatorV3Interface(_temperatureFeed),
            humidityFeed: AggregatorV3Interface(_humidityFeed),
            rainfallFeed: AggregatorV3Interface(_rainfallFeed),
            windSpeedFeed: AggregatorV3Interface(_windSpeedFeed),
            priceFeed: AggregatorV3Interface(_priceFeed)
        });

        // Get initial oracle data
        (
            Weather.WeatherData memory currentWeather,
            uint256 currentPrice
        ) = _getOracleDataSafe();

        estimatedPrice = currentPrice;

        // Create initial farm stage data
        productStages[ProductStage.FARM] = StageData({
            stakeholder: farmer,
            timestamp: createdAt,
            data: _farmData,
            dataHash: keccak256(abi.encodePacked(_farmData)),
            weatherAtStage: currentWeather,
            marketPriceAtStage: currentPrice
        });

        // Check conditions and emit events
        _checkConditionsAndEmitEvents(currentWeather, currentPrice);
    }

    /**
     * @dev Update processing stage
     */
    function updateProcessingStage(
        string calldata _processingData
    )
        external
        onlyRegisteredStakeholder(Stakeholder.StakeholderRole.PROCESSOR)
        validStageTransition(ProductStage.PROCESSING)
        nonEmptyString(_processingData)
    {
        _updateProductStage(ProductStage.PROCESSING, _processingData);
    }

    /**
     * @dev Update distribution stage
     */
    function updateDistributionStage(
        string calldata _distributionData
    )
        external
        onlyRegisteredStakeholder(Stakeholder.StakeholderRole.DISTRIBUTOR)
        validStageTransition(ProductStage.DISTRIBUTION)
        nonEmptyString(_distributionData)
    {
        _updateProductStage(ProductStage.DISTRIBUTION, _distributionData);
    }

    /**
     * @dev Update retail stage
     */
    function updateRetailStage(
        string calldata _retailData
    )
        external
        onlyRegisteredStakeholder(Stakeholder.StakeholderRole.RETAILER)
        validStageTransition(ProductStage.RETAIL)
        nonEmptyString(_retailData)
    {
        _updateProductStage(ProductStage.RETAIL, _retailData);
    }

    /**
     * @dev Internal function to update stage
     */
    function _updateProductStage(
        ProductStage _stage,
        string calldata _data
    ) internal {
        uint32 currentTime = uint32(block.timestamp);

        // Get oracle data
        (
            Weather.WeatherData memory currentWeather,
            uint256 currentPrice
        ) = _getOracleDataSafe();

        // Update product state
        currentStage = _stage;
        estimatedPrice = currentPrice;

        // Update stage data
        productStages[_stage] = StageData({
            stakeholder: msg.sender,
            timestamp: currentTime,
            data: _data,
            dataHash: keccak256(abi.encodePacked(_data)),
            weatherAtStage: currentWeather,
            marketPriceAtStage: currentPrice
        });

        // Check conditions and emit events
        _checkConditionsAndEmitEvents(currentWeather, currentPrice);

        emit ProductStageUpdated(
            _stage,
            msg.sender,
            _data,
            currentTime,
            currentWeather,
            currentPrice
        );
    }

    /**
     * @dev Mark product as consumed
     */
    function markAsConsumed() external {
        require(
            currentStage == ProductStage.RETAIL,
            "Product must be at retail stage"
        );

        currentStage = ProductStage.CONSUMED;

        emit ProductStageUpdated(
            ProductStage.CONSUMED,
            msg.sender,
            "Product consumed",
            uint32(block.timestamp),
            Weather.WeatherData(0, 0, 0, 0, uint32(block.timestamp)),
            0
        );
    }

    /**
     * @dev Verify product data integrity
     */
    function verifyProduct() external view returns (bool isValid) {
        bool dataValid = true;

        for (uint i = 0; i <= uint(currentStage); i++) {
            ProductStage stage = ProductStage(i);
            StageData memory stageData = productStages[stage];

            if (stageData.timestamp > 0) {
                bytes32 expectedHash = keccak256(
                    abi.encodePacked(stageData.data)
                );
                if (expectedHash != stageData.dataHash) {
                    dataValid = false;
                    break;
                }
            }
        }

        return dataValid;
    }

    /**
     * @dev Get stage data
     */
    function getStageData(
        ProductStage _stage
    ) external view returns (StageData memory) {
        return productStages[_stage];
    }

    /**
     * @dev Get complete product journey
     */
    function getProductJourney()
        external
        view
        returns (
            StageData memory farmStage,
            StageData memory processingStage,
            StageData memory distributionStage,
            StageData memory retailStage
        )
    {
        return (
            productStages[ProductStage.FARM],
            productStages[ProductStage.PROCESSING],
            productStages[ProductStage.DISTRIBUTION],
            productStages[ProductStage.RETAIL]
        );
    }

    /**
     * @dev Get basic product info
     */
    function getProductInfo()
        external
        view
        returns (
            string memory productName,
            string memory productDescription,
            address productFarmer,
            uint32 productCreatedAt,
            ProductStage productCurrentStage,
            bool productIsActive,
            uint256 productEstimatedPrice,
            string memory productLocation
        )
    {
        return (
            name,
            description,
            farmer,
            createdAt,
            currentStage,
            isActive,
            estimatedPrice,
            location
        );
    }

    // Oracle and condition checking functions (same as ProductRegistry)
    function _getOracleDataSafe()
        internal
        view
        returns (Weather.WeatherData memory weather, uint256 price)
    {
        weather = _getWeatherDataSafe();
        price = _getPriceSafe();
    }

    function _getWeatherDataSafe()
        internal
        view
        returns (Weather.WeatherData memory)
    {
        if (address(oracleFeeds.temperatureFeed) == address(0)) {
            return
                Weather.WeatherData(
                    2000,
                    5000,
                    0,
                    1000,
                    uint32(block.timestamp)
                );
        }

        return
            Weather.getWeatherData(
                oracleFeeds.temperatureFeed,
                oracleFeeds.humidityFeed,
                oracleFeeds.rainfallFeed,
                oracleFeeds.windSpeedFeed
            );
    }

    function _getPriceSafe() internal view returns (uint256) {
        if (address(oracleFeeds.priceFeed) == address(0)) {
            return 500000;
        }
        return Price.getPrice(oracleFeeds.priceFeed);
    }

    function _checkConditionsAndEmitEvents(
        Weather.WeatherData memory _weather,
        uint256 _currentPrice
    ) internal {
        uint32 currentTime = uint32(block.timestamp);

        // Check weather conditions
        if (_weather.temperature < 1500 || _weather.temperature > 2500) {
            emit WeatherAlert(
                "TEMPERATURE",
                _weather.temperature < 1500
                    ? "Temperature too low"
                    : "Temperature too high",
                _weather,
                currentTime
            );
        }

        if (_weather.humidity < 4000 || _weather.humidity > 6000) {
            emit WeatherAlert(
                "HUMIDITY",
                _weather.humidity < 4000
                    ? "Humidity too low"
                    : "Humidity too high",
                _weather,
                currentTime
            );
        }

        if (_weather.rainfall > 5000) {
            emit WeatherAlert(
                "RAINFALL",
                "Heavy rainfall detected",
                _weather,
                currentTime
            );
        }

        // Check price conditions
        _checkPriceConditions(_currentPrice);
    }

    function _checkPriceConditions(uint256 _currentPrice) internal {
        if (uint(currentStage) == 0) {
            return;
        }

        uint256 oraclePrice = _getPriceSafe();
        uint256 prevPrice = _getPreviousStagePrice();

        _checkOraclePriceChange(oraclePrice, prevPrice);
        _checkStageOracleDifference(_currentPrice, oraclePrice);
    }

    function _getPreviousStagePrice() internal view returns (uint256) {
        ProductStage prevStage = ProductStage(uint(currentStage) - 1);
        return productStages[prevStage].marketPriceAtStage;
    }

    function _checkOraclePriceChange(
        uint256 _oraclePrice,
        uint256 _prevPrice
    ) internal {
        uint256 threshold = _prevPrice / 10; // 10% threshold
        uint32 currentTime = uint32(block.timestamp);

        if (_oraclePrice > _prevPrice + threshold) {
            emit PriceAlert(
                _oraclePrice,
                threshold,
                "ORACLE_PRICE_INCREASE",
                currentTime
            );
        } else if (_oraclePrice < _prevPrice - threshold) {
            emit PriceAlert(
                _oraclePrice,
                threshold,
                "ORACLE_PRICE_DECREASE",
                currentTime
            );
        }
    }

    function _checkStageOracleDifference(
        uint256 _stagePrice,
        uint256 _oraclePrice
    ) internal {
        uint256 threshold = _oraclePrice / 20; // 5% threshold
        uint256 difference = _stagePrice > _oraclePrice
            ? _stagePrice - _oraclePrice
            : _oraclePrice - _stagePrice;

        if (difference > threshold) {
            string memory alertType = _stagePrice > _oraclePrice
                ? "STAGE_PRICE_ABOVE_ORACLE"
                : "STAGE_PRICE_BELOW_ORACLE";

            emit PriceAlert(
                _oraclePrice,
                threshold,
                alertType,
                uint32(block.timestamp)
            );
        }
    }
}
","keccak256":"0x285a2d49a3f5a4684c41ad049b95a9fd7f4924b11d14c70bf5dbbcdcde59358b","license":"MIT"},"src/SmartContracts/PublicVerification.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./Product.sol";
import "./Shipment.sol";
import "./StakeholderRegistry.sol";
import "./Stakeholder.sol";
import "./Registry.sol";

contract PublicVerification {
    StakeholderRegistry public stakeholderRegistry;
    Registry public registry;

    // Define the StakeholderInfo struct locally since it's used in return types
    struct StakeholderInfo {
        address stakeholderAddress;
        Stakeholder.StakeholderRole role;
        string businessName;
        string businessLicense;
        string location;
        string certifications;
        bool isActive;
        uint256 registeredAt;
        uint256 lastActivity;
    }

    event ProductVerificationRequested(
        address indexed productAddress,
        address indexed verifier,
        uint256 timestamp
    );
    event VerificationResult(
        address indexed productAddress,
        bool isAuthentic,
        string details,
        uint256 timestamp
    );
    event AuditPerformed(
        address indexed auditor,
        address indexed productAddress,
        string auditResult,
        uint256 timestamp
    );
    event ShipmentVerificationPerformed(
        address indexed shipmentAddress,
        address indexed productAddress,
        bool isValid,
        uint256 timestamp
    );

    constructor(address _stakeholderRegistryAddress, address _registryAddress) {
        stakeholderRegistry = StakeholderRegistry(_stakeholderRegistryAddress);
        registry = Registry(_registryAddress);
    }

    function verifyProductAuthenticity(
        address _productAddress
    ) external returns (bool isAuthentic, string memory details) {
        emit ProductVerificationRequested(
            _productAddress,
            msg.sender,
            block.timestamp
        );

        try Product(_productAddress).verifyProduct() returns (bool valid) {
            if (valid) {
                bool stakeholdersValid = true;
                string memory invalidReason = "";

                Product product = Product(_productAddress);
                address farmer = product.farmer();

                if (
                    !stakeholderRegistry.isRegisteredStakeholder(
                        farmer,
                        Stakeholder.StakeholderRole.FARMER
                    )
                ) {
                    stakeholdersValid = false;
                    invalidReason = "Farmer registration invalid";
                }

                Product.ProductStage currentStage = product.currentStage();

                if (
                    stakeholdersValid &&
                    currentStage >= Product.ProductStage.PROCESSING
                ) {
                    Product.StageData memory processingStage = product
                        .getStageData(Product.ProductStage.PROCESSING);
                    if (
                        processingStage.timestamp > 0 &&
                        !stakeholderRegistry.isRegisteredStakeholder(
                            processingStage.stakeholder,
                            Stakeholder.StakeholderRole.PROCESSOR
                        )
                    ) {
                        stakeholdersValid = false;
                        invalidReason = "Processor registration invalid";
                    }
                }

                if (
                    stakeholdersValid &&
                    currentStage >= Product.ProductStage.DISTRIBUTION
                ) {
                    Product.StageData memory distributionStage = product
                        .getStageData(Product.ProductStage.DISTRIBUTION);
                    if (
                        distributionStage.timestamp > 0 &&
                        !stakeholderRegistry.isRegisteredStakeholder(
                            distributionStage.stakeholder,
                            Stakeholder.StakeholderRole.DISTRIBUTOR
                        )
                    ) {
                        stakeholdersValid = false;
                        invalidReason = "Distributor registration invalid";
                    }
                }

                if (
                    stakeholdersValid &&
                    currentStage >= Product.ProductStage.RETAIL
                ) {
                    Product.StageData memory retailStage = product.getStageData(
                        Product.ProductStage.RETAIL
                    );
                    if (
                        retailStage.timestamp > 0 &&
                        !stakeholderRegistry.isRegisteredStakeholder(
                            retailStage.stakeholder,
                            Stakeholder.StakeholderRole.RETAILER
                        )
                    ) {
                        stakeholdersValid = false;
                        invalidReason = "Retailer registration invalid";
                    }
                }

                if (stakeholdersValid) {
                    details = "Product is authentic and all stakeholders verified";
                    emit VerificationResult(
                        _productAddress,
                        true,
                        details,
                        block.timestamp
                    );
                    return (true, details);
                } else {
                    details = string(
                        abi.encodePacked(
                            "Product data valid but ",
                            invalidReason
                        )
                    );
                    emit VerificationResult(
                        _productAddress,
                        false,
                        details,
                        block.timestamp
                    );
                    return (false, details);
                }
            } else {
                details = "Product data integrity compromised";
                emit VerificationResult(
                    _productAddress,
                    false,
                    details,
                    block.timestamp
                );
                return (false, details);
            }
        } catch {
            details = "Product not found or verification failed";
            emit VerificationResult(
                _productAddress,
                false,
                details,
                block.timestamp
            );
            return (false, details);
        }
    }

    function verifyCompleteSupplyChain(
        address _productAddress
    ) external returns (bool isValid, string memory details) {
        (bool productValid, string memory productDetails) = this
            .verifyProductAuthenticity(_productAddress);

        if (!productValid) {
            return (false, productDetails);
        }

        address shipmentAddress = findShipmentByProduct(_productAddress);

        if (shipmentAddress != address(0)) {
            Shipment shipment = Shipment(shipmentAddress);
            Shipment.ShipmentStatus status = shipment.status();

            if (
                status == Shipment.ShipmentStatus.CANCELLED ||
                status == Shipment.ShipmentStatus.UNABLE_TO_DELIVERED
            ) {
                emit ShipmentVerificationPerformed(
                    shipmentAddress,
                    _productAddress,
                    false,
                    block.timestamp
                );
                return (false, "Product valid but shipment has issues");
            } else {
                emit ShipmentVerificationPerformed(
                    shipmentAddress,
                    _productAddress,
                    true,
                    block.timestamp
                );
                return (
                    true,
                    "Product and shipment both verified successfully"
                );
            }
        } else {
            return (true, "Product verified, no shipment data available");
        }
    }

    function getTraceabilityReport(
        address _productAddress
    )
        external
        view
        returns (
            string memory productName,
            address farmer,
            StakeholderInfo memory farmerInfo,
            StakeholderInfo memory processorInfo,
            StakeholderInfo memory distributorInfo,
            StakeholderInfo memory retailerInfo,
            bool isFullyTraced
        )
    {
        try Product(_productAddress).getProductJourney() returns (
            Product.StageData memory farmStage,
            Product.StageData memory processingStage,
            Product.StageData memory distributionStage,
            Product.StageData memory retailStage
        ) {
            Product product = Product(_productAddress);
            productName = product.name();
            farmer = product.farmer();

            StakeholderInfo memory farmerStakeholder;
            StakeholderInfo memory processor;
            StakeholderInfo memory distributor;
            StakeholderInfo memory retailer;

            if (farmStage.timestamp > 0) {
                (
                    address addr,
                    Stakeholder.StakeholderRole role,
                    string memory name,
                    string memory license,
                    string memory location,
                    string memory certs,
                    bool active,
                    uint256 registered,
                    uint256 activity
                ) = stakeholderRegistry.getStakeholderInfo(
                        farmStage.stakeholder
                    );

                farmerStakeholder = StakeholderInfo({
                    stakeholderAddress: addr,
                    role: role,
                    businessName: name,
                    businessLicense: license,
                    location: location,
                    certifications: certs,
                    isActive: active,
                    registeredAt: registered,
                    lastActivity: activity
                });
            }

            if (processingStage.timestamp > 0) {
                (
                    address addr,
                    Stakeholder.StakeholderRole role,
                    string memory name,
                    string memory license,
                    string memory location,
                    string memory certs,
                    bool active,
                    uint256 registered,
                    uint256 activity
                ) = stakeholderRegistry.getStakeholderInfo(
                        processingStage.stakeholder
                    );

                processor = StakeholderInfo({
                    stakeholderAddress: addr,
                    role: role,
                    businessName: name,
                    businessLicense: license,
                    location: location,
                    certifications: certs,
                    isActive: active,
                    registeredAt: registered,
                    lastActivity: activity
                });
            }

            if (distributionStage.timestamp > 0) {
                (
                    address addr,
                    Stakeholder.StakeholderRole role,
                    string memory name,
                    string memory license,
                    string memory location,
                    string memory certs,
                    bool active,
                    uint256 registered,
                    uint256 activity
                ) = stakeholderRegistry.getStakeholderInfo(
                        distributionStage.stakeholder
                    );

                distributor = StakeholderInfo({
                    stakeholderAddress: addr,
                    role: role,
                    businessName: name,
                    businessLicense: license,
                    location: location,
                    certifications: certs,
                    isActive: active,
                    registeredAt: registered,
                    lastActivity: activity
                });
            }

            if (retailStage.timestamp > 0) {
                (
                    address addr,
                    Stakeholder.StakeholderRole role,
                    string memory name,
                    string memory license,
                    string memory location,
                    string memory certs,
                    bool active,
                    uint256 registered,
                    uint256 activity
                ) = stakeholderRegistry.getStakeholderInfo(
                        retailStage.stakeholder
                    );

                retailer = StakeholderInfo({
                    stakeholderAddress: addr,
                    role: role,
                    businessName: name,
                    businessLicense: license,
                    location: location,
                    certifications: certs,
                    isActive: active,
                    registeredAt: registered,
                    lastActivity: activity
                });
            }

            Product.ProductStage currentStage = product.currentStage();
            bool fullyTraced = (farmStage.timestamp > 0 &&
                (currentStage == Product.ProductStage.FARM ||
                    processingStage.timestamp > 0) &&
                (currentStage <= Product.ProductStage.PROCESSING ||
                    distributionStage.timestamp > 0) &&
                (currentStage <= Product.ProductStage.DISTRIBUTION ||
                    retailStage.timestamp > 0));

            return (
                productName,
                farmer,
                farmerStakeholder,
                processor,
                distributor,
                retailer,
                fullyTraced
            );
        } catch {
            // Return empty data for non-existent products
            StakeholderInfo memory emptyStakeholder;
            return (
                "",
                address(0),
                emptyStakeholder,
                emptyStakeholder,
                emptyStakeholder,
                emptyStakeholder,
                false
            );
        }
    }

    function getCompleteTraceabilityReport(
        address _productAddress
    )
        external
        view
        returns (
            string memory productName,
            address farmer,
            StakeholderInfo memory farmerInfo,
            StakeholderInfo memory processorInfo,
            StakeholderInfo memory distributorInfo,
            StakeholderInfo memory retailerInfo,
            bool isFullyTraced,
            bool hasShipment,
            address shipmentAddress,
            Shipment.ShipmentUpdate[] memory shipmentHistory
        )
    {
        (
            productName,
            farmer,
            farmerInfo,
            processorInfo,
            distributorInfo,
            retailerInfo,
            isFullyTraced
        ) = this.getTraceabilityReport(_productAddress);

        shipmentAddress = findShipmentByProduct(_productAddress);
        if (shipmentAddress != address(0)) {
            hasShipment = true;
            Shipment shipment = Shipment(shipmentAddress);
            shipmentHistory = shipment.getShipmentHistory();
        }

        return (
            productName,
            farmer,
            farmerInfo,
            processorInfo,
            distributorInfo,
            retailerInfo,
            isFullyTraced,
            hasShipment,
            shipmentAddress,
            shipmentHistory
        );
    }

    function trackShipmentByTrackingNumber(
        string memory _trackingNumber
    )
        external
        view
        returns (
            address shipmentAddress,
            address productAddress,
            Product.ProductStage productStage,
            Shipment.ShipmentStatus shipmentStatus,
            string memory productName,
            string memory statusDescription,
            bool isProductValid,
            bool isShipmentValid
        )
    {
        shipmentAddress = findShipmentByTrackingNumber(_trackingNumber);
        require(
            shipmentAddress != address(0),
            "Invalid tracking number or shipment not found"
        );

        Shipment shipment = Shipment(shipmentAddress);
        productAddress = shipment.productAddress();
        shipmentStatus = shipment.status();
        statusDescription = shipment.getStatusDescription();

        Product product = Product(productAddress);
        productStage = product.currentStage();
        productName = product.name();

        isProductValid = product.verifyProduct();

        isShipmentValid = !(shipmentStatus ==
            Shipment.ShipmentStatus.CANCELLED ||
            shipmentStatus == Shipment.ShipmentStatus.UNABLE_TO_DELIVERED);

        return (
            shipmentAddress,
            productAddress,
            productStage,
            shipmentStatus,
            productName,
            statusDescription,
            isProductValid,
            isShipmentValid
        );
    }

    function performAudit(
        address _productAddress,
        string memory _auditResult
    ) external {
        require(
            stakeholderRegistry.isActiveStakeholder(msg.sender),
            "Only registered stakeholders can perform audits"
        );

        emit AuditPerformed(
            msg.sender,
            _productAddress,
            _auditResult,
            block.timestamp
        );
    }

    function getShipmentInfo(
        address _shipmentAddress
    )
        external
        view
        returns (
            address product,
            address sender,
            address receiver,
            string memory trackingNumber,
            string memory transportMode,
            Shipment.ShipmentStatus status,
            uint256 createdAt,
            uint256 lastUpdated,
            bool isActive
        )
    {
        require(
            registry.isEntityRegistered(_shipmentAddress),
            "Shipment not registered"
        );

        Shipment shipment = Shipment(_shipmentAddress);
        return shipment.getShipmentInfo();
    }

    // Helper functions to find shipments (application-layer logic)
    function findShipmentByProduct(
        address _productAddress
    ) public view returns (address) {
        address[] memory allShipments = registry.getAllShipments();

        for (uint256 i = 0; i < allShipments.length; i++) {
            try Shipment(allShipments[i]).productAddress() returns (
                address productAddr
            ) {
                if (productAddr == _productAddress) {
                    return allShipments[i];
                }
            } catch {
                // Skip invalid shipments
                continue;
            }
        }

        return address(0);
    }

    function findShipmentByTrackingNumber(
        string memory _trackingNumber
    ) public view returns (address) {
        address[] memory allShipments = registry.getAllShipments();

        for (uint256 i = 0; i < allShipments.length; i++) {
            try Shipment(allShipments[i]).trackingNumber() returns (
                string memory trackingNum
            ) {
                if (
                    keccak256(bytes(trackingNum)) ==
                    keccak256(bytes(_trackingNumber))
                ) {
                    return allShipments[i];
                }
            } catch {
                // Skip invalid shipments
                continue;
            }
        }

        return address(0);
    }
}
","keccak256":"0x4b1d307321b0aea2eb1214c9e67d2cb01351f7a832c30c7d70597c9496cd6501","license":"MIT"},"src/SmartContracts/Registry.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./Stakeholder.sol";

contract Registry {

    address[] public products;
    address[] public shipments;
    address[] public stakeholders;
    mapping(address => bool) public isRegistered;

    // Stakeholder-specific mappings for efficient lookups
    mapping(string => address) public licenseToStakeholder;
    mapping(address => address) public walletToStakeholderContract;
    mapping(Stakeholder.StakeholderRole => address[]) public stakeholdersByRole;

    event ShipmentRegistered(
        address indexed _shipment,
        string indexed trackingNumber,
        address indexed productAddress,
        address sender,
        address receiver
    );
    event ProductRegistered(address indexed _product);
    event StakeholderRegistered(
        address indexed _stakeholderContract,
        string indexed businessLicense,
        address indexed stakeholderAddress,
        Stakeholder.StakeholderRole role
    );

    function registerShipment(
        address _shipment,
        string memory _trackingNumber,
        address _productAddress,
        address _sender,
        address _receiver
    ) public {
        require(!isRegistered[_shipment], "Shipment already registered");
        isRegistered[_shipment] = true;
        shipments.push(_shipment);
        emit ShipmentRegistered(
            _shipment,
            _trackingNumber,
            _productAddress,
            _sender,
            _receiver
        );
    }

    function registerProduct(address _product) public {
        require(!isRegistered[_product], "Product already registered");
        isRegistered[_product] = true;
        products.push(_product);
        emit ProductRegistered(_product);
    }

    function registerStakeholder(
        address _stakeholderContract,
        string memory _businessLicense,
        address _stakeholderAddress,
        Stakeholder.StakeholderRole _role
    ) public {
        require(
            !isRegistered[_stakeholderContract],
            "Stakeholder already registered"
        );
        require(
            licenseToStakeholder[_businessLicense] == address(0),
            "Business license already registered"
        );
        require(
            walletToStakeholderContract[_stakeholderAddress] == address(0),
            "Stakeholder address already has a contract"
        );

        isRegistered[_stakeholderContract] = true;
        stakeholders.push(_stakeholderContract);
        licenseToStakeholder[_businessLicense] = _stakeholderContract;
        walletToStakeholderContract[_stakeholderAddress] = _stakeholderContract;
        stakeholdersByRole[_role].push(_stakeholderContract);

        emit StakeholderRegistered(
            _stakeholderContract,
            _businessLicense,
            _stakeholderAddress,
            _role
        );
    }

    function getAllProducts() external view returns (address[] memory) {
        return products;
    }

    function getAllShipments() external view returns (address[] memory) {
        return shipments;
    }

    function getAllStakeholders() external view returns (address[] memory) {
        return stakeholders;
    }

    function getStakeholdersByRole(
        Stakeholder.StakeholderRole _role
    ) external view returns (address[] memory) {
        return stakeholdersByRole[_role];
    }

    function getStakeholderByLicense(
        string memory _businessLicense
    ) external view returns (address) {
        return licenseToStakeholder[_businessLicense];
    }

    function getStakeholderByWallet(
        address _stakeholderAddress
    ) external view returns (address) {
        return walletToStakeholderContract[_stakeholderAddress];
    }

    function getTotalProducts() external view returns (uint256) {
        return products.length;
    }

    function getTotalShipments() external view returns (uint256) {
        return shipments.length;
    }

    function isEntityRegistered(address _entity) external view returns (bool) {
        return isRegistered[_entity];
    }
}
","keccak256":"0x5bafb18393bd73a4871c6ced1b98d88758ec095627c0a3db710c592b9a98ca22","license":"MIT"},"src/SmartContracts/Shipment.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./StakeholderRegistry.sol";
import "./Product.sol";
import "./Stakeholder.sol";

contract Shipment {
    enum ShipmentStatus {
        NOT_SHIPPED,
        PREPARING,
        SHIPPED,
        DELIVERED,
        CANCELLED,
        UNABLE_TO_DELIVERED,
        VERIFIED
    }

    struct ShipmentUpdate {
        uint256 timestamp;
        ShipmentStatus status;
        address updater;
        string trackingInfo;
        string location;
    }

    // Basic shipment info
    address public productAddress;
    address public sender;
    address public receiver;
    string public trackingNumber;
    string public transportMode;
    
    // State tracking
    ShipmentStatus public status;
    uint256 public createdAt;
    uint256 public lastUpdated;
    bool public isActive;

    // History tracking
    ShipmentUpdate[] public shipmentHistory;
    
    // External contracts
    StakeholderRegistry public immutable stakeholderRegistry;

    // Events
    event ShipmentStatusUpdated(
        ShipmentStatus indexed newStatus,
        address indexed updater,
        string trackingInfo,
        string location,
        uint256 timestamp
    );

    event ShipmentDelivered(
        address indexed receiver,
        uint256 timestamp
    );

    event ShipmentCancelled(
        string reason,
        uint256 timestamp
    );

    // Modifiers
    modifier onlyRegisteredStakeholder(
        Stakeholder.StakeholderRole _requiredRole
    ) {
        require(
            stakeholderRegistry.isRegisteredStakeholder(
                msg.sender,
                _requiredRole
            ),
            "Not registered for this role"
        );
        _;
    }

    modifier onlyShipmentParticipant() {
        require(
            msg.sender == sender ||
                msg.sender == receiver ||
                stakeholderRegistry.isRegisteredStakeholder(
                    msg.sender,
                    Stakeholder.StakeholderRole.DISTRIBUTOR
                ),
            "Not authorized for this shipment"
        );
        _;
    }

    modifier validProductForShipment(address _productAddress) {
        Product product = Product(_productAddress);
        require(product.isActive(), "Product is not active");
        
        Product.ProductStage currentStage = product.currentStage();
        require(
            currentStage == Product.ProductStage.PROCESSING ||
            currentStage == Product.ProductStage.DISTRIBUTION ||
            currentStage == Product.ProductStage.RETAIL,
            "Product not ready for shipment"
        );
        _;
    }

    /**
     * @dev Constructor creates a new shipment
     * @notice Tracking number uniqueness should be validated at the factory level
     * @notice Product shipment eligibility is validated via modifier
     */
    constructor(
        address _productAddress,
        address _sender,
        address _receiver,
        string memory _trackingNumber,
        string memory _transportMode,
        address _stakeholderRegistry
    ) validProductForShipment(_productAddress) {
        require(_productAddress != address(0), "Invalid product address");
        require(_sender != address(0), "Invalid sender address");
        require(_receiver != address(0), "Invalid receiver address");
        require(bytes(_trackingNumber).length > 0, "Tracking number cannot be empty");

        productAddress = _productAddress;
        sender = _sender;
        receiver = _receiver;
        trackingNumber = _trackingNumber;
        transportMode = _transportMode;
        status = ShipmentStatus.PREPARING;
        createdAt = block.timestamp;
        lastUpdated = block.timestamp;
        isActive = true;

        stakeholderRegistry = StakeholderRegistry(_stakeholderRegistry);

        // Add initial status to history
        shipmentHistory.push(
            ShipmentUpdate({
                timestamp: block.timestamp,
                status: ShipmentStatus.PREPARING,
                updater: _sender,
                trackingInfo: "Shipment created and preparing",
                location: "Origin facility"
            })
        );

        emit ShipmentStatusUpdated(
            ShipmentStatus.PREPARING,
            _sender,
            "Shipment created and preparing",
            "Origin facility",
            block.timestamp
        );
    }

    /**
     * @dev Update shipment status
     */
    function updateStatus(
        ShipmentStatus _newStatus,
        string memory _trackingInfo,
        string memory _location
    ) public onlyShipmentParticipant {
        require(
            _isValidShipmentTransition(status, _newStatus),
            "Invalid shipment status transition"
        );

        status = _newStatus;
        lastUpdated = block.timestamp;

        shipmentHistory.push(
            ShipmentUpdate({
                timestamp: block.timestamp,
                status: _newStatus,
                updater: msg.sender,
                trackingInfo: _trackingInfo,
                location: _location
            })
        );

        emit ShipmentStatusUpdated(
            _newStatus,
            msg.sender,
            _trackingInfo,
            _location,
            block.timestamp
        );

        if (_newStatus == ShipmentStatus.DELIVERED) {
            emit ShipmentDelivered(receiver, block.timestamp);
        }

        if (_newStatus == ShipmentStatus.CANCELLED) {
            emit ShipmentCancelled(_trackingInfo, block.timestamp);
        }
    }

    /**
     * @dev Cancel shipment with reason
     */
    function cancel(string memory _reason) external onlyShipmentParticipant {
        require(
            status == ShipmentStatus.PREPARING || status == ShipmentStatus.SHIPPED,
            "Cannot cancel shipment in current status"
        );

        updateStatus(ShipmentStatus.CANCELLED, _reason, "");
    }

    /**
     * @dev Verify delivery
     */
    function verifyDelivery() external {
        require(msg.sender == receiver, "Only receiver can verify delivery");
        require(status == ShipmentStatus.DELIVERED, "Shipment must be delivered first");

        updateStatus(ShipmentStatus.VERIFIED, "Delivery verified by receiver", "");
    }

    /**
     * @dev Get complete shipment history
     */
    function getShipmentHistory() external view returns (ShipmentUpdate[] memory) {
        return shipmentHistory;
    }

    /**
     * @dev Get latest update
     */
    function getLatestUpdate() external view returns (ShipmentUpdate memory) {
        require(shipmentHistory.length > 0, "No updates available");
        return shipmentHistory[shipmentHistory.length - 1];
    }

    /**
     * @dev Get shipment info
     */
    function getShipmentInfo()
        external
        view
        returns (
            address product,
            address shipmentSender,
            address shipmentReceiver,
            string memory tracking,
            string memory transport,
            ShipmentStatus currentStatus,
            uint256 created,
            uint256 updated,
            bool active
        )
    {
        return (
            productAddress,
            sender,
            receiver,
            trackingNumber,
            transportMode,
            status,
            createdAt,
            lastUpdated,
            isActive
        );
    }

    /**
     * @dev Get status description
     */
    function getStatusDescription() external view returns (string memory) {
        return _getShipmentStatusDescription(status);
    }

    /**
     * @dev Internal function to validate status transitions
     */
    function _isValidShipmentTransition(
        ShipmentStatus _current,
        ShipmentStatus _new
    ) internal pure returns (bool) {
        if (_current == ShipmentStatus.NOT_SHIPPED) {
            return
                _new == ShipmentStatus.PREPARING ||
                _new == ShipmentStatus.CANCELLED;
        } else if (_current == ShipmentStatus.PREPARING) {
            return
                _new == ShipmentStatus.SHIPPED ||
                _new == ShipmentStatus.CANCELLED;
        } else if (_current == ShipmentStatus.SHIPPED) {
            return
                _new == ShipmentStatus.DELIVERED ||
                _new == ShipmentStatus.UNABLE_TO_DELIVERED;
        } else if (_current == ShipmentStatus.DELIVERED) {
            return _new == ShipmentStatus.VERIFIED;
        }
        return false;
    }

    /**
     * @dev Internal function to get status description
     */
    function _getShipmentStatusDescription(
        ShipmentStatus _status
    ) internal pure returns (string memory) {
        if (_status == ShipmentStatus.NOT_SHIPPED) return "Not yet shipped";
        if (_status == ShipmentStatus.PREPARING) return "Preparing for shipment";
        if (_status == ShipmentStatus.SHIPPED) return "In transit";
        if (_status == ShipmentStatus.DELIVERED) return "Delivered";
        if (_status == ShipmentStatus.CANCELLED) return "Shipment cancelled";
        if (_status == ShipmentStatus.UNABLE_TO_DELIVERED) return "Delivery failed";
        if (_status == ShipmentStatus.VERIFIED) return "Delivery confirmed";
        return "Unknown status";
    }
